var robosem_res;
var item;
var res;
var apphash;
var res_robosem;
var res_motion_checker;
var motion_checker_res;
var result;
var message;
var hue_id;
var hue_color;

function check_room_ex(res) {
  $engine.runScheduledAction(res, "check_motion", "rocon_ninja_block/CheckMotionAction", ({"device_name":''}), function(item){   $engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Result'));if (item['success'] == true) {
      for (var result_index in  item['is_detected']) {
        result = item['is_detected'][result_index];
        message = 'empty';
        hue_id = 1;
        hue_color = 'RED';
        if (result['key'] == 'motion_detector') {
          hue_id = 1;
        } else if (result['key'] == 'reception_room') {
          hue_id = 2;
        } else {
          $engine.print("Rocon Authoring :" + JSON.stringify('Unknown key'));$engine.print("Rocon Authoring :" + JSON.stringify(result['key']));}
        if (result['value'] == 'True') {
          message = 'occupied';
          hue_color = 'RED';
        } else if (result['value'] == 'False') {
          message = 'empty';
          hue_color = 'GREEN';
        } else {
          $engine.print("Rocon Authoring :" + JSON.stringify('Unknown value'));$engine.print("Rocon Authoring :" + JSON.stringify(result['value']));}
        $engine.print("Rocon Authoring :" + JSON.stringify([result['key'],': ',message].join('')));UDF_set_light([hue_id], hue_color, null, null, hue_color);}
    }
  }, function(item){ });}

function start_app(apphash) {
  $engine.pub('/start_app', 'std_msgs/String', ({data:apphash}));}

function welcome(res_robosem, res_motion_checker) {
  setTimeout(function(){ Fiber(function(){  $engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'유징 로봇에 오신 여러분을 환영합니다.'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'HAPPY3'}));UDF_set_light([2, 1], 'BLUE', null, null, 'BLUE');$engine.sleep(4000);UDF_set_light([2, 1], 'WHITE', null, null, 'WHITE');$engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'회의실을 체크할께요'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'HAPPY3'}));$engine.sleep(4000);$engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'빨간불이 켜진 방이 현재 사용중이고, '}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'HAPPY3'}));check_room_ex(res_motion_checker);
    $engine.sleep(3000);$engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'녹색불이 켜진 방이 비어있는 회의실 입니다.'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'HAPPY3'}));$engine.sleep(6000);$engine.scheduledPublish(res_robosem, "play_tts", "std_msgs/String", ({"data":'그럼 문을 열어 놓을께요. 안녕히가세요'}));$engine.scheduledPublish(res_robosem, "robot_motion", "std_msgs/String", ({"data":'HAPPY3'}));$engine.pub('/door_ctrl', 'std_msgs/Bool', ({data:true}));$engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Welcome End'));$engine.sleep(3000);UDF_set_light([2, 1], 'WHITE', null, null, 'WHITE');$engine.sleep(6000);}).run(); }, 0);}


robosem_res = ($engine.allocateResource("robosem_bridge/robosem_bridge", "rocon:/pc/robosem_bridge#robosem_bridge/robosem_bridge", [{"remap_from":"play_tts","remap_to":"/play_tts"},{"remap_from":"robot_motion","remap_to":"/robot_motion"},{"remap_from":"touch_sensor_event","remap_to":"/touch_sensor_event"}], [])) ;
motion_checker_res = ($engine.allocateResource("rocon_ninja_block/motion_checker", "rocon:/pc/motion_checker#rocon_ninja_block/motion_checker", [{"remap_from":"ninja_block_data","remap_to":"/ninja_block_data"},{"remap_from":"check_motion","remap_to":"/check_motion"}], [])) ;

$engine.scheduledSubscribe(robosem_res, 'touch_sensor_event', 'std_msgs/String', function(item){   if (item['data'] == '4') {
    welcome(robosem_res, motion_checker_res);
  } else if (item['data'] == '3') {
    welcome(robosem_res, motion_checker_res);
  } else {
  }
 });
$engine.subscribe('/beacons', 'std_msgs/String'); $engine.ee.on('/beacons', function(item){   $engine.print("Rocon Authoring :" + JSON.stringify(['[Welcome] Beacon aware!!:','   ',item['data']].join('')));if (item['data'] == 'D0:39:72:A3:DE:75') {
    welcome(robosem_res, motion_checker_res);
  } else if (item['data'] == '88:33:14:DE:D0:ED') {
    $engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Start Order App Conf. Room'));start_app('-322740222');
  } else if (item['data'] == 'D0:FF:50:66:93:3F') {
    $engine.print("Rocon Authoring :" + JSON.stringify('[Welcome] Start Order App Meeting Room'));start_app('-249900251');
  }
 });
